                        return <div  key={item._id}>
                                    
                        <TimeEntryForm
                            timeEntry={item}
                            // timeEntries={timeEntries}
                            onDelete={this.handleDelete}
                            onSave={this.handleSave}
                        />
                        </div>
                    })}
                </div>
            </div>
        )
    }
}

export default TimeEntries;
